mapper.select:
/* if函数，如果传输的 status为 1，则正常，否则转成 0*/
select from_id as sendId, to_id as receiverId, if(status = 1,1,0) as status
from im_friendship
where app_id = #{appId} and from_id = #{sendId} and to_id in

    #{id}

select a.sendId, a.receiverId, ( case when a.status = 1 and b.status = 1 then 1 /*用户 a 和 b 是好友，用户 b 和 a 是好友，为双向好友*/ when a.status = 1 and b.status != 1 then 2 /*用户 a 和 b 是好友，用户 b 和 a 不是好友，为单向好友*/ when a.status != 1 and b.status = 1 then 3 /*用户 a 和 b 不是好友，用户 b 和 a 是好友，为单向好友*/ when a.status != 1 and b.status != 1 then 4 /*用户 a 和 b 不是好友，用户 b 和 a 不是好友，为双向非好友*/ end) as status from ( select from_id as sendId, to_id as receiverId, if(status = 1,1,0) as status from im_friendship where app_id = #{appId} from_id = #{sendId} and to_id in
#{id}
) as a inner join ( select from_id as sendId, to_id as receiverId, if(status = 1,1,0) as status from im_friendship where app_id = #{appId} to_id = #{sendId} and from_id in
#{id}
) as b on a.sendId = b.receiverId and b.sendId = a.receiverId

select a.sendId, a.receiverId, ( case when a.black = 1 and b.black = 1 then 1 /*用户 a 和 b 是好友，用户 b 和 a 是好友，为双向好友*/ when a.black = 1 and b.black != 1 then 2 /*用户 a 和 b 是好友，用户 b 和 a 不是好友，为单向好友*/ when a.black != 1 and b.black = 1 then 3 /*用户 a 和 b 不是好友，用户 b 和 a 是好友，为单向好友*/ when a.black != 1 and b.black != 1 then 4 /*用户 a 和 b 不是好友，用户 b 和 a 不是好友，为双向非好友*/ end ) as status from ( select from_id as sendId, to_id as receiverId, if(black = 1,1,0) as black from im_friendship where app_id = #{appId} from_id = #{sendId} and to_id in
#{id}
) as a inner join ( select from_id as sendId, to_id as receiverId, if(black = 1,1,0) as black from im_friendship where app_id = #{appId} to_id = #{sendId} and from_id in
#{id}
) as b on a.sendId = b.receiverId and b.sendId = a.receiverId
